select.where:
and def_id = 0
 and id = #{id}
 and sign = #{sign}
  and company_id = #{companyId}
  and product_code  like concat(#{productCode}, '%') 
  and product_name like concat(#{productName}, '%') 

    AND date_format(create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


    AND date_format(create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

 and product_model like concat(#{productModel}, '%') 
 and price = #{price}
 and product_img = #{productImg}
 and img_size = #{imgSize}
 and rule_id = #{ruleId}

mapper.update:
update dev_product_list
product_code = #{productCode}, product_name = #{productName}, remark = #{remark}, product_model = #{productModel}, price = #{price}, product_img = #{productImg}, img_size = #{imgSize}, standard_hour_yield = #{standardHourYield}, ecnstatus = #{ecnStatus}, ecn_text = #{ecnText}, unit = #{unit}, rule_id = #{ruleId},
where id = #{id}

mapper.select:
select id as Id,product_code as productCode,product_model as productModel,product_name as productName,ecnstatus as ecnStatus, sign as  sign from dev_product_list
 where company_id = #{companyId} and def_id = 0

      and sign=#{sign}

SELECT * FROM dev_product_list where company_id = #{companyId}

    and product_code = #{productCode}


    and id = #{id}


    and sign = #{sign}


    and rule_id = #{ruleId}
