mapper.select:
SELECT b.id as id,b.workshop_name as workshopName,b.im_code as imCode,b.watch_code as watchCode,b.jpush_tag as jpushTag
FROM tab_single_work_order as a,tab_single_work as b where a.single_id = b.id and b.watch_id != 0

    and a.work_id = #{workId}


    and a.single_p = #{singleP}


    and b.jpush_tag = #{jpushTag}


    and b.im_code like concat (#{imCode},'%')

mapper.update:
update tab_single_work
company_id = #{companyId}, workshop_name = #{workshopName}, im_id = #{imId}, im_code = #{imCode}, sign = #{sign}, liable_one = #{liableOne}, liable_two = #{liableTwo}, dev_id = #{devId}, dev_code = #{devCode}, watch_id = #{watchId}, watch_code = #{watchCode}, e_id = #{eId}, e_code = #{eCode}, c_time = #{cTime}, parent_id = #{parentId}, sop_sign = #{sopSign}, jpush_tag = #{jpushTag},
where id = #{id}

update tab_single_work
company_id = #{companyId}, workshop_name = #{workshopName}, im_id = #{imId}, im_code = #{imCode}, sign = #{sign}, liable_one = #{liableOne}, liable_two = #{liableTwo}, c_time = #{cTime}, parent_id = #{parentId}, sop_sign = #{sopSign}, jpush_tag = #{jpushTag},
where id = #{id}

mapper.select:
SELECT * FROM tab_single_work

    and dev_id = #{dev_id}
    and watch_id = #{watch_id}
    and e_id = #{e_id}

  limit 1;

SELECT * FROM tab_single_work

    and dev_code = #{devCode}
    and watch_code = #{watchCode}

limit 1;

SELECT * FROM tab_single_work as a left join tab_single_work_order as b
on a.id = b.single_id and b.work_id = #{order_id}
  where a.parent_id =#{pid} and b.work_id is null;

SELECT * FROM tab_single_work as a  left join tab_sop_line_work as b on a.id = b.w_id
and b.line_id = #{parentId} and b.sop_tag = #{sopTag} and b.sop_id = #{sopId}
and b.company_id = #{companyId} where a.parent_id = #{parentId} and b.w_id is null;
